INSERT INTO `#prefix#_bbcodes` (`bbcode_id`, `bbcode_tag`, `bbcode_helpline`, `display_on_posting`, `bbcode_match`, `bbcode_tpl`, `first_pass_match`, `first_pass_replace`, `second_pass_match`, `second_pass_replace`) VALUES
(13, 'youtube', 'Youtube Video:[youtube]Video Number[/youtube]', 1, '[youtube]{SIMPLETEXT}[/youtube]', '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/{SIMPLETEXT}?rel=0&amp;controls=0&amp;showinfo=0\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen></iframe>', '#\\[youtube\\]([a-zA-Z0-9-+.,_ ]+)\\[/youtube\\]#uis', '[youtube:$uid]${1}[/youtube:$uid]', '#\\[youtube:$uid\\]([a-zA-Z0-9-+.,_ ]+)\\[/youtube:$uid\\]#uis', '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/${1}?rel=0&amp;controls=0&amp;showinfo=0\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen></iframe>'),
(14, 'spoiler', 'Add Spoiler Box:[spoiler]Text to Hide[/spoiler]', 1, '[spoiler]{TEXT}[/spoiler]', '<div class=\"shout\"><div class=\"shout\"><a href=\"#\" onclick=\"n=this.parentNode.parentNode.lastChild; if(n.style.display == \'none\') {n.style.display = \'block\'; } else { n.style.display = \'none\';} return false;\">Spoiler (Click to Show/Hide)</a></div><div class=\"spoiler\" style=\"display: none;\">{TEXT}</div></div>', '#\\[spoiler\\](.*?)\\[/spoiler\\]#uis', '[spoiler:$uid]${1}[/spoiler:$uid]', '#\\[spoiler:$uid\\](.*?)\\[/spoiler:$uid\\]#uis', '<div class=\"shout\"><div class=\"shout\"><a href=\"#\" onclick=\"n=this.parentNode.parentNode.lastChild; if(n.style.display == \'none\') {n.style.display = \'block\'; } else {	n.style.display = \'none\';} return false;\">Spoiler (Click to Show/Hide)</a></div><div class=\"spoiler\" style=\"display: none;\">${1}</div></div>'),
(15, 'highlight=', 'Highlight Text: [highlight=Color]Text[/highlight]', 1, '[highlight={COLOR}]{TEXT}[/highlight]', '<span style=\"background-color: {COLOR};\">{TEXT}</span>', '#\\[highlight\\=(\\#[^0-9a-f]{3}|\\#[0-9a-f]{6}|[a-z\\-]+)\\](.*?)\\[/highlight\\]#uis', '[highlight=${1}:$uid]${2}[/highlight:$uid]', '#\\[highlight\\=(\\#[^0-9a-f]{3}|\\#[0-9a-f]{6}|[a-z\\-]+):$uid\\](.*?)\\[/highlight:$uid\\]#uis', '<span style=\"background-color: ${1};\">${2}</span>'),
(16, 's', 'Strike Through Your Text:[s]strikethrough text[/s]', 1, '[s]{TEXT}[/s]', '<span style=\"text-decoration: line-through;\">{TEXT}</span>', '#\\[s\\](.*?)\\[/s\\]#uis', '[s:$uid]${1}[/s:$uid]', '#\\[s:$uid\\](.*?)\\[/s:$uid\\]#uis', '<span style=\"text-decoration: line-through;\">${1}</span>'),
(17, 'mod=', '[mod=username]Message to Display[/mod]', 0, '[mod={SIMPLETEXT1}]{SIMPLETEXT2}[/mod]', '<div style=\"background:#f8f8f8;color:#222;border:1px solid #999;padding:10px;float:left;\"><strong><em>Moderator Message:</em></strong><br />{SIMPLETEXT2}<br /><a href=\"http://CHANGE-THIS-ON-INSTALL.com/forum/memberlist.php?mode=viewprofile&un={SIMPLETEXT1}\" style=\"text-align:right;display:block;\"><span style=\"color:#222;\">-</span> {SIMPLETEXT1}</a></div><div style=\"clear:both;\"></div>', '#\\[mod\\=([a-zA-Z0-9-+.,_ ]+)\\]([a-zA-Z0-9-+.,_ ]+)\\[/mod\\]#uis', '[mod=${1}:$uid]${2}[/mod:$uid]', '#\\[mod\\=([a-zA-Z0-9-+.,_ ]+):$uid\\]([a-zA-Z0-9-+.,_ ]+)\\[/mod:$uid\\]#uis', '<div style=\"background:#f8f8f8;color:#222;border:1px solid #999;padding:10px;float:left;\"><strong><em>Moderator Message:</em></strong><br />${2}<br /><a href=\"http://CHANGE-THIS-ON-INSTALL.com/forum/memberlist.php?mode=viewprofile&un=${1}\" style=\"text-align:right;display:block;\"><span style=\"color:#222;\">-</span> ${1}</a></div><div style=\"clear:both;\"></div>');